var menu = document.getElementById('menu'),
    menuLink = document.getElementById('menuLink'),
    layout = document.getElementById('container'),

    toggleClass = function (element, className) {
        var classes = element.className.split(/\s+/),
            length = classes.length,
            i = 0;

        for(; i < length; i++) {
            if (classes[i] === className) {
                classes.splice(i, 1);
                break;
            }
        }
        // The className is not found
        if (length === classes.length) {
            classes.push(className);
        }

        element.className = classes.join(' ');
    };

menuLink.onclick = function (e) {
    e.preventDefault();
    var active = 'active';
    toggleClass(layout, active);
    toggleClass(menu, active);
    return false;
};

jQuery(document).ready(function(w){
    w('#loginform').submit(function(){
        w.ajax({
            url:w(this).attr('action'),
            type:w(this).attr('method'),
            data:w(this).serialize(),
            dataType:'json',
            success:function(d){
                if(d.status==false)
                    w('#loginform #msg').html('<span class="round alert label">'+ d.msg +'</span>');
                else
                    window.location=base_url+'admin/';
            }
        });
        return false;
    });
});