jQuery(document).ready(function(w){
    w('#loginform').submit(function(){
        w.ajax({
            url:w(this).attr('action'),
            type:w(this).attr('method'),
            data:w(this).serialize(),
            dataType:'json',
            success:function(d){
                if(d.status==false)
                    w('#loginform #msg').html('<span class="round alert label">'+ d.msg +'</span>');
                else
                    window.location=base_url+'admin/';
            }
        });
        return false;
    });
});